import React, { useState } from 'react'
import Persons from './Components/Persons'

const checkDuplicate = ({ name, persons }) => {
  if (persons.map(person => person.name).indexOf(name) === -1) {
    return false
  } else {
    return true
  }
}

const PersonForm = ({ onSubmit, nameValue, numberValue, nameChange, numberChange }) => {
  return (
    <form onSubmit={onSubmit}>
    <div>
      Name: <input 
              value={nameValue}
              onChange={nameChange}
            />
    </div>
    <div>
      Number: <input
                value={numberValue}
                onChange={numberChange}
              />
    </div>
    <div>
      <button type="submit">Add</button>
    </div>
    </form>
  )
}


const App = () => {
  const [persons, setPersons] = useState([])
  const [display, setDisplay] = useState([])
  const [newName, setNewName ] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const filterPersons = () => {
    if (filter !== '') {
      setDisplay(persons.filter(person => person.name.toLowerCase().includes(filter.toLowerCase())))
      setFilter('')
    } else {
      setDisplay(inputArray)
    }
  }

  const addPerson = (event) => {
    event.preventDefault()
    if (!checkDuplicate( newName, persons )) {
      const nameObject = {
        name: newName,
        number: newNumber,
        id: persons.length,
      }
      setPersons(persons.concat(nameObject))
      setDisplay(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')
    } else {
      window.alert(`${newName} has already been added to the phonebook!`)
    }
  }

  const handleNameChange = (event) => {
      setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    setFilter(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      Filter by name: <input
                        value={filter}
                        onChange={handleFilterChange}
                      />
      <button type="submit" onClick={filterPersons( filter, persons )}>Filter</button>
      <h3>Add a new name</h3>
      <PersonForm onSubmit={addPerson} nameValue={newName} numberValue={newNumber} nameChange={handleNameChange} numberChange={handleNumberChange} />
      <form onSubmit={addPerson}>
        <div>
          Name: <input 
                  value={newName}
                  onChange={handleNameChange}
                />
        </div>
        <div>
          Number: <input
                    value={newNumber}
                    onChange={handleNumberChange}
                  />
        </div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h3>Numbers</h3>
      <Persons data={display} />
    </div>
  )
}

export default App
